Select DEPT_LOCATIONS.Dnumber,DEPARTMENT.Dname,count(Distinct DEPT_LOCATIONS.Dlocation) from DEPT_LOCATIONS JOIN DEPARTMENT ON DEPARTMENT.Dnumber=DEPT_LOCATIONS.Dnumber JOIN DEPENDENT ON DEPARTMENT.Mgr_ssn=DEPENDENT.Essn where DEPENDENT.Sex='F' Group By DEPT_LOCATIONS.Dnumber having count(DEPENDENT.Sex)>=2;
